mapper.select:
select pl_idx, user_id1, user_id2, pl_money, pl_date
	from pay_list
where pl_idx = #{plIdx}

select pd_idx, user_id1, user_id2, pd_money
from partner_detail
where pd_idx = #{pdIdx}

select pc_idx, pc_content, pc_writer, pd_idx
from partner_content
where pd_idx = #{pdIdx}

mapper.update:
update partner_content
   set pc_writer = #{pcWriter}, 
       pc_content = #{pcContent} 

 where pc_idx = #{pcIdx}

mapper.select:
select pdc_comment, user_id, pdc_idx, pc_idx
from partner_detail_comment
where pc_idx = #{pcIdx}

mapper.update:
update partner_detail_comment
   set user_id = #{userId}, 
       pc_content = #{pcContent} 


 where pc_idx = #{pcIdx}

mapper.delete:
update partner_detail_comment
   set deleted_yn = 'Y' 


 where pc_idx = #{pcIdx}
