mapper.select:
select pc_idx, pc_content, pc_writer, pd_idx
from partner_content
where pd_idx = #{pdIdx}

select pc_idx, pc_content, pc_writer, pc_file, pd_idx
from partner_content
where pc_idx = #{pcIdx}

mapper.update:
update partner_content
   set pc_content = #{pcContent},
		pc_file = #{pcFile},
		pc_idx = #{pcIdx}
 where pc_idx = #{pcIdx}

mapper.delete:
update partner_content
   set deleted_yn = 'Y' 
 where pc_idx = #{pcIdx}

update partner_detail_comment
   set deleted_yn = 'Y'  
 where pdc_idx = #{pdcIdx}
